  def test_homepage(self):
    self.driver.get("http://127.0.0.1:5500/index.html")
    self.driver.set_window_size(1217, 692)
    WebDriverWait(self.driver, 20).until(expected_conditions.visibility_of_element_located((By.LINK_TEXT, "Home")))
    self.driver.find_element(By.LINK_TEXT, "Home").click()
    elements = self.driver.find_elements(By.CSS_SELECTOR, ".header-logo img")
    assert len(elements) > 0
    assert self.driver.find_element(By.CSS_SELECTOR, ".header-title > h1").text == "Teton Idaho"
    assert self.driver.title == "Teton Idaho CoC"
    elements = self.driver.find_elements(By.CSS_SELECTOR, ".spotlight1")
    assert len(elements) > 0
    elements = self.driver.find_elements(By.CSS_SELECTOR, ".spotlight2")
    assert len(elements) > 0
    self.driver.find_element(By.LINK_TEXT, "Join Us!").click()
    assert self.driver.find_element(By.CSS_SELECTOR, "h3").text == "Welcome to the Teton Chamber of Commerce Signup Wizard!"
  
